import { APICitas, APIUsers } from "../../../../../api/api";
import { Alert, Button, Form, Modal } from "react-bootstrap";
import { useState } from "react";

const ModalApprovedCita = ({
  show,
  handleClose,
  selectedCita,
  setAlert,
  setAlertMsg,
  setAlertVariant,
  message,
}) => {
  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    console.log(res);
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert("Notificación al usuario hecha y cita aprovada.", "success");
    }
  };

  const handleDelete = async () => {
    const res = await APICitas.ApproveCita({
      cita: selectedCita,
    });
    checkResponse(res);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Aprovar Cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>{`¿Está seguro que desa aprovar y notificar al cliente de la cita con fecha : ${selectedCita.appointment_date}?`}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={handleDelete}>
          Aprovar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

const ModalRemoveCita = ({
  show,
  handleClose,
  selectedCita,
  setAlert,
  setAlertMsg,
  setAlertVariant,
  message,
  messageAlert,
}) => {
  const handleAlert = (msg, variant) => {
    setAlert(true);
    setAlertMsg(msg);
    setAlertVariant(variant);
    setTimeout(() => {
      setAlert(false);
      setAlertMsg("");
      setAlertVariant("");
    }, 3000);
    handleClose();
  };

  const checkResponse = (res) => {
    if (res[1].status === 400) {
      handleAlert(res[0]["message"], "danger");
    } else {
      handleAlert(`${messageAlert}`, "success");
    }
  };

  const handleDelete = async () => {
    const res = await APICitas.deleteCitaNotification({
      cita: selectedCita,
    });
    checkResponse(res);
  };

  return (
    <Modal show={show} onHide={handleClose}>
      <Modal.Header closeButton>
        <Modal.Title>Eliminar Cita</Modal.Title>
      </Modal.Header>
      <Modal.Body>{`¿${message}: ${selectedCita.appointment_date}?`}</Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={handleClose}>
          Cancelar
        </Button>
        <Button variant="primary" onClick={handleDelete}>
          Eliminar
        </Button>
      </Modal.Footer>
    </Modal>
  );
};

